import React, {useRef, useState} from 'react';
import {useContainerDimensions} from "../../../../helpers/hooks/useContainerDimensions";
import ServiceButton from "../ServiceButton";
import ServiceText from "../ServiceText";

function ServicePhone({handleChoices}) {
    const buttonRef = useRef(null);
    const servicesRef = useRef(null);
    const widthButton = useContainerDimensions(buttonRef).width;
    const heightServices = useContainerDimensions(servicesRef).height;


    const components =  handleChoices.providePricesList().map((element, index) => {
        return (
            <ServiceText key={element.id} id={element.id} title={element.title} price={element.price}
                         handleChoices={handleChoices}/>
        )
    });
    const [showChoices, setShow] = useState(false)
    return (
        <div style={{
            overflow:"hidden"
        }}>
            <ServiceButton onClick={() => setShow(!showChoices)} ref={buttonRef}/>
            <div style={{
                position: "absolute",
                zIndex: 1,
            }}>
                {showChoices && <div style={{
                    backgroundColor: "white",
                    width: widthButton,
                    borderRadius: "10px",
                    border:"1px solid #EAE9F2",
                }}>
                    <div ref={servicesRef}>
                        {components}
                    </div>

                </div>
                }

            </div>
        </div>
    );
}

export default ServicePhone;
