import React from 'react';
import {StyleSheet, css} from 'aphrodite';

const Text = ({
                  fontWeight = 600,
                  text,
                  color,
                  size,
                  hover = {
                      cursor: "p"
                  }
              }) => {
    const style = StyleSheet.create({
            spanText: {
                fontFamily: "Gilroy",
                fontWeight: fontWeight,
                color: color,
                fontSize: size,
            },
            addHover: {
                ":hover": hover
            }
        }
    );

    return (
        <span className={css(style.spanText, style.addHover)} onClick={() => console.log("Asdsad")}>
            {text}
        </span>
    );
};

export default Text;
