import React from 'react';
import {css, StyleSheet} from 'aphrodite';
import Text from "../../../helpers/Text";
import ImagesAnimation from "./ImagesAnimation";
import Buttons from "../../../helpers/Buttons";

const HelloAndImage = () => {
    const style = StyleSheet.create({
        containerRow: {
            flex: 1,
            alignItems: "center",
            
        },
        firstColumn: {
            flex: 2,
            textAlign: "start"
        },
        secondColumn: {
            flex: 2,
        },
    })

    return (
        <div className="row alItCenter">
            <div className={css(style.firstColumn)}>
                <Text text="Улучшайте или создавайте ваш бизнес с IT B&SH"
                      color="#121212"
                      fontWeight={700}
                      size={"3rem"}/>
                <br/>
                <Text text="Мы современная IT компания которая поможет вам начать свой путь в техническом мире"
                      color={"#121212"} fontWeight={500} size={"1.3rem"}/>
                <br/><br/>
                <Buttons nameOfButton={"Подробнее"}/>
            </div>
            <div style={{
                flex: 0.5
            }}/>
            <div className={css(style.secondColumn)}>
                <ImagesAnimation/>
            </div>
        </div>
    );
};

export default HelloAndImage;
