import React, {useState} from 'react';
import Images from "../Images";
import Paginate from "../../../helpers/scroll/Paginate";

function MobileImages({portfolio}) {
    const paginate = new Paginate(3, useState(0))

    function selectedImage(index) {
        return paginate.isSelected(index) ? "5rem" : "25rem";
    }
    const listImages = [
        portfolio.imageFirst,
        portfolio.imageSecond,
        portfolio.imageThird
    ]
    console.log(listImages);
    const listComponent = [];
    for (let i = 0; i < listImages.length; i++) {
        listComponent.push(
            <div onClick={() => paginate.goPageDecide(i)}>
                <Images heightBox={selectedImage(i)} height={""} src={listImages[i]}/>
            </div>
        )
    }
    return (
        <>
            <Images heightBox={selectedImage(0)} height={""} src={portfolio.imageFirst}/>
            <Images heightBox={selectedImage(1)} height={""} src={portfolio.imageSecond}/>
            <Images heightBox={selectedImage(2)} height={""} src={portfolio.imageThird}/>
            <div style={{
                margin: "20px"
            }}/>
        </>
    );
}

export default MobileImages;
