import React, {useRef, useState} from 'react';
import Images from "../../Images";
import Paginate from "../../../../helpers/scroll/Paginate";
import DescriptionPortfolio from "../../hover/DescriptionPortfolio";
import {useContainerDimensions} from "../../../../helpers/hooks/useContainerDimensions";
import {HandleImageComponents} from "./HandleImageComponents";

function MobileImages(props) {
    const paginate = new Paginate(3, useState(0))
    const imageHelper = new HandleImageComponents(useRef(null), props.portfolio, paginate);
    const {width, height} = useContainerDimensions(imageHelper.refs)

    const listComponent = imageHelper.getImageComponents();
    const style = {
        border: "1px solid #979797",
        borderBottomRightRadius: "8px",
        borderBottomLeftRadius: "8px",
        marginTop: "-2.3rem"
    }

    return (
        <>
            {listComponent}
            <DescriptionPortfolio width={width - 1} body={props.portfolio.body} title={props.portfolio.title}
                                  additional_style={style}/>
            <div style={{
                margin: "20px"
            }}/>
        </>
    );
}

export default MobileImages;
