import React, {useState} from 'react';
import Comment from "./Comment";
import {css, StyleSheet} from 'aphrodite';
import ScrollButton from "../../helpers/scroll/ScrollButton";
import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../responsive/Responsive";
import {Axis} from "../../helpers/scroll/helperScrollClass";
import Scrolling from "../../helpers/scroll/Scrolling";
import Paginate from "../../helpers/scroll/Paginate";
import {StoreReviews} from "./store/StoreReviews";

function Comments(props) {
    const style = StyleSheet.create({
        edgesMargin: {
            marginRight: "10%",
            marginLeft: "10%",
            marginTop: "2.5%"
        }
    });
    const mobile = useMediaQuery(
        RESPONSIVE.MOBILE
    )
    const paginate = new Paginate(3, useState(0));
    const desktop = !mobile;
    const componentList = StoreReviews.map((e, i) => {
       return <Comment {...e}/>
    });
    return (
        <>
            <div className={css(style.edgesMargin) + " row"}>{componentList}</div>
            <Scrolling paginate={paginate}/>
        </>
    );
}

export default Comments;
