import React from 'react';

import Title from "../../../helpers/text/Title";
import Text from "../../../helpers/text/Text";
import Buttons from "../../../helpers/Buttons";
import person from "../../../../image/person_icon.svg";
import email from "../../../../image/email.svg"
function ModalRemainReview({isOpen}) {
    const style = {
        padding:"2rem",

        margin:"auto auto"
    }
    return (
        <div  style={style}>
            <div className="col ">
                <Title title={"Комментарий"} center={true}/>
                <br/>
                <input placeholder={"Имя"} style={{
                    background: `url(${person}) no-repeat scroll`,
                    backgroundPositionX:"0.7em",
                    backgroundPositionY:"0.3em",
                    backgroundSize:"1.2em",
                    paddingLeft:"2.5em"
                }}/>

                <input placeholder={"Профессия"} style={{
                    background: `url(${email}) no-repeat scroll`,
                    backgroundPositionX:"1em",
                    backgroundPositionY:"0.3em",
                    backgroundSize:"1.2em",
                    paddingLeft:"2.5em"
                }}/>

                <textarea  style={{
                    resize:"none"
                }}/>
                <br/>
                <Buttons nameOfButton="Отправить комментарий" width="" height="2.3rem"/>
            </div>

        </div>
    );
}

export default ModalRemainReview;
