import React, {useRef, useState} from 'react';
import {useContainerDimensions} from "../../../helpers/hooks/useContainerDimensions";
import ServiceButton from "./ServiceButton";

function ServicePhone({components},ref) {
    const myRef = useRef(null);
    const width = useContainerDimensions(myRef).width;
    const [showChoices, setShow] = useState(false)
    return (
        <div style={{
            overflow:"hidden"
        }}>
            <ServiceButton onClick={() => setShow(!showChoices)} ref={myRef}/>
            <div style={{
                position: "absolute",
                zIndex: 1,
            }}>
                {showChoices && <div style={{
                    backgroundColor: "white",
                    width: width,
                    borderRadius: "8px",
                    height: "20rem"
                }}>
                    {components(width)}
                </div>
                }

            </div>
        </div>
    );
}

export default ServicePhone;
