import React from 'react';
import Service from "./Service";
import ServiceText from "./ServiceText";
import {HandleChoices} from "../choices/HandleChoices";
import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../../responsive/Responsive";
import DropDownService from "../DropDownService";

function Services({handleChoices}) {

    const pricesList = handleChoices.providePricesList();

    const components = pricesList.map((element, index) => {
        return (
            <ServiceText key={element.id} id={element.id} title={element.title} price={element.price}
                         handleChoices={handleChoices}/>
        )
    });

    const mobile = useMediaQuery(RESPONSIVE.MOBILE);

    const desktop = !mobile;

    const service = desktop ? <Services handleChoices={handleChoices}/> :
        <DropDownService handleChoices={handleChoices}>{components}</DropDownService>
    return (
        <div className="row" style={{
            flex: "2",
            alignContent: "flex-start",
            flexWrap: "wrap"
        }}>
            {components}
        </div>
    );
}

export default Services;
