import React from 'react';

import Images from "../Images";

import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../../responsive/Responsive";
import Scrolling from "../../../helpers/scroll/Scrolling";
import HoverActions from "../hover/HoverActions";
import {useState} from "react";
import Paginate from "../../../helpers/scroll/Paginate";


function RowImages({portfolios}) {
    const mobile = useMediaQuery(
        RESPONSIVE.MOBILE
    )
    console.log(portfolios);
    const responsive = mobile ? "col alItCenter" : "row alItCenter";
    const componentList = [];

    for (let i = 0; i < portfolios.length; i++) {
         const desktop  =    <>
             <Images flex="0.25"  src={portfolios[i].imageFirst}/>
             <HoverActions flex={"1"} child={<Images src={portfolios[i].imageSecond}/>}
                           title={portfolios[i].title} body={portfolios[i].body}
             />
             <Images flex="0.25" src={portfolios[i].imageThird}/>
             <div style={{
                 margin: "20px"
             }}/>
         </>
        const mobileComponent = <>
            <Images heightBox={"5rem"} height={""}  src={portfolios[i].imageFirst}/>
            <Images heightBox={"5rem"} height={""}  src={portfolios[i].imageSecond}/>
            <Images heightBox={"25rem"} height={""} src={portfolios[i].imageThird}/>
            <div style={{
                margin: "20px"
            }}/>
        </>
        componentList.push(desktop)

    }
    const paginate = new Paginate(componentList.length, useState(0));
    return (
        <>
            <div className={responsive}>
                {componentList.filter((i, number) => number === paginate.currentPage)}
                <Scrolling length={componentList.length} paginate={paginate} onClick={() => {
                }}/>
            </div>
        </>
    );
}

export default RowImages;
