import React from 'react';
import Text from "../../../helpers/Text";

function HoverText(props) {
    const style = {
        position:"absolute",
        bottom:0,
        marginLeft:"0.5rem",
        marginRight:"0.5rem",
        left:0,
        width:"100%",
        backgroundColor:"gray"
    }
    return (
        <div style={style}>
            <Text text={"TITLE"} fontWeight={700}/>
            <br/>
            <Text text="Those medals you wear on your moth-eaten chest should be there for bungling at which you are best."/>
        </div>
    );
}

export default HoverText;
