import React from 'react';
import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../responsive/Responsive";
import ScrollButton, {Axis} from "./ScrollButton";

function Scrolling({index, length, onClick}) {
    const mobile = useMediaQuery(
        RESPONSIVE.MOBILE
    )
    const responsive = mobile ? "row jccenter" : "";
    const axis = mobile ? Axis.HORIZONTAL : Axis.VERTICAL;

    return (
        <div className={responsive}>
            <ScrollButton onClick={() => onClick(0)} axis={axis} selected={0 === index}/>
            {length >= 2 &&
            <ScrollButton axis={axis} onClick={() => onClick(index - 1)} selected={1 === index}/>}
            {length >= 3 &&
            <ScrollButton axis={axis} selected={(length === index - 2) || (length === 3 && length === index - 1)}/>}
            {length >= 4 &&
            <ScrollButton axis={axis} onClick={() => onClick(length - 1)} selected={length === index - 1}/>}
        </div>
    );
}

export default Scrolling;
