import React, {useRef} from 'react';
import ServiceButton from "./service/ServiceButton";
import {useContainerDimensions} from "../../helpers/hooks/useContainerDimensions";

function DropDownService(props) {
    const myRef = useRef();
    const {width, height} = useContainerDimensions(myRef);
    return (
        <div>
            <ServiceButton ref={myRef}/>
            <div style={{
                position: "absolute",
                zIndex: 1,
            }}>
                <div style={{
                    color: 'white',
                    backgroundColor: "gray",
                    width: width + 2,
                    height: "20rem"
                }}>

                </div>
            </div>
        </div>
    );
}

export default DropDownService;
