import React from 'react';
import {StyleSheet, css} from 'aphrodite';

function ScrollButton({selected = false}) {

    const style = StyleSheet.create({
        common: {
            marginBottom:'0.2rem',
        },
        active: {
            height: "3rem",
            width: "0.35rem",
            borderRadius: "8px",
            backgroundColor: "#5454D4"
        },
        inactive: {
            height: "1rem",
            width: "7px",
            borderRadius: "8px",
            backgroundColor: "#EAE9F2"
        },
        addHover: {
            ":hover": {}
        }
    });
    return (
        <div className={css (selected?style.active: style.inactive, style.margin)}></div>
    );
}

export default ScrollButton;
