import React from 'react';
import {StyleSheet, css} from 'aphrodite';

export const Axis = {
    HORIZONTAL: "h",
    VERTICAL: "v"
}

function ScrollButton({selected = false, axis = Axis.VERTICAL}) {

    function selectAppropriateStyle(select, active, inactive) {
        return select ? active : inactive;
    }

    function selectAppropriateAxes(axis, horizontal, vertical, list = [], ...styles){
        const choosen = axis === Axis.HORIZONTAL ? horizontal : vertical;
        list.push(choosen);
       return  list.concat(styles);
    }

    const mainAxisActive = "3rem";
    const mainAxisInActive = "1.8em";
    const crossAxis = "0.35rem";

    const style = StyleSheet.create({
        common: {
            marginBottom: '0.5rem',
            borderRadius: "8px",
        },
        vertical: {
            width: crossAxis,
        },
        horizontal: {
            height: crossAxis,
        },
        active: {
            backgroundColor: "#5454D4"
        },
        activeHorizontal: {
            width: mainAxisActive
        },
        activeVertical: {
            height: mainAxisActive,
        },
        inactive: {
            backgroundColor: "#EAE9F2"
        },
        inactiveVertical: {
            height: mainAxisInActive,
        },
        inactiveHorizontal: {
            width: mainAxisInActive,
        },
        addHover: {
            ":hover": {}
        }
    });

    const list = [];
    const usedAxis = css(
        selectAppropriateAxes(
            axis,
            selectAppropriateStyle(selected, style.activeHorizontal, style.inactiveHorizontal, list, style.horizontal), // choose horizontal
            selectAppropriateStyle(selected, style.activeVertical, style.inactiveVertical, list, style.horizontal)
        ), // or vertical styling
        selectAppropriateStyle(selected, style.active, style.inactive), style.common
    )
    return (
        <div className={usedAxis}></div>
    );
}

export default ScrollButton;
