import React from 'react';

import {StyleSheet, css} from 'aphrodite';
import TextHover from "./TextHover";
import {Link} from "react-router-dom";
import TextLink from "../routes/TextLink";
import {Routing} from "../routes/Routes";

const Header = ({
                    main = false,
                    portfolio = false,
                    prices = false,
                    reviews = false,
                    contact_us = false,
                }) => {
    const containerRowStyle = StyleSheet.create({
        row: {
            flex: 1,
            "display": "flex",
            flexDirection: "row",
            alignItems: "start"
        },
        spaceWidth: {
            marginLeft: "1.5%",
            marginRight: "1.5%"
        },

    });
    const activatedColor = "#D24A6A";
    const deactivatedColor = "#696871";

    function chooseColor(choice) {
        return choice ? activatedColor : deactivatedColor;
    }

    return (
        <div className={css(containerRowStyle.row)}>
            <TextLink text="Лого" color="#696871" fontWeight="400"/>
            <span className={css(containerRowStyle.spaceWidth)}/>
            <span className={css(containerRowStyle.spaceWidth)}/>
            <TextLink to={Routing.MAIN} text="Главная" color={chooseColor(main)} fontWeight="600"/>
            <span className={css(containerRowStyle.spaceWidth)}/>
            <TextLink to={Routing.PORTFOLIO} text="Портфолио" color={chooseColor(portfolio)} fontWeight="600"/>
            <span className={css(containerRowStyle.spaceWidth)}/>
            <TextLink to={Routing.SERVICES} text="Услуги" color={chooseColor(prices)} fontWeight="600"/>
            <span className={css(containerRowStyle.spaceWidth)}/>
            <TextLink to={Routing.REVIEWS} text="Отзывы" color={chooseColor(reviews)} fontWeight="600"/>
            <div className={css(containerRowStyle.spaceWidth)}/>
            <TextLink to={Routing.CONTACT_US} text="Контакты" color={chooseColor(contact_us)} fontWeight="600"/>

        </div>
    );
};

export default Header;
