import React from 'react';
import Text from "./Text";

function TextHover({
                       fontWeight = 600,
                       text,
                       color = "#D24A6A",
                       size,
                   }) {
    return (
        <>
            <Text fontWeight={fontWeight} text={text} color={color} size={size} hover={{
                cursor: "pointer",
                color: color
            }}/>
        </>
    );
}

export default TextHover;
