import React, {useRef} from 'react';
import Service from "./Service";
import ServiceText from "./ServiceText";
import {HandleChoices} from "../choices/HandleChoices";
import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../../responsive/Responsive";
import DropDownService from "../DropDownService";
import ServicePhone from "./mobile/ServicePhone";
import ServiceDesktop from "./desktop/ServiceDesktop";
import {useContainerDimensions} from "../../../helpers/hooks/useContainerDimensions";

function Services({handleChoices}) {

    const pricesList = handleChoices.providePricesList();



    const components = width => pricesList.map((element, index) => {
        return (
            <ServiceText key={element.id} id={element.id} width={width}  title={element.title} price={element.price}
                         handleChoices={handleChoices}/>
        )
    });


    const mobile = useMediaQuery(RESPONSIVE.MOBILE);

    const desktop = !mobile;

    const service = desktop ? <ServiceDesktop components={components} /> :
        <ServicePhone components={components}/>
    return (
        <>
            {service}
        </>

    );
}

export default Services;
