import Images from "../../Images";
import React from "react";


export class HandleImageComponents {
    constructor(refs, portfolio, paginate) {
        this.refs = refs;
        this.listImages = [
            portfolio.imageFirst,
            portfolio.imageSecond,
            portfolio.imageThird
        ];
        this.paginate = paginate;
    }

    getImageComponents() {
        return this.listImages.map(this.getImages);
    }


    getImages(image, i) {
        return (
            <div key={i} onClick={() => this.paginate.goPageDecide(i)}>
                <Images ref={this.provideRefLastImage(i)} heightBox={this.selectedImage(i)} height={""}
                        borderRadiusBottomOff={this.removeRadiusForLastImage(i)} src={image}/>
            </div>
        )
    }

    provideRefLastImage(index) {
        return index === 2 ? this.refs : null;
    }

    removeRadiusForLastImage(index) {
        return index === 2;
    }

    selectedImage(index) {
        return this.paginate.isSelected(index) ? "18rem" : "5rem";
    }
}
