import React, {useRef, useState} from 'react';
import {useContainerDimensions} from "../../../helpers/hooks/useContainerDimensions";
import ServiceButton from "./ServiceButton";

function ServicePhone({children , width},ref) {

    const [showChoices, setShow] = useState(false)
    return (
        <div style={{
            overflow:"hidden"
        }}>
            <ServiceButton onClick={() => setShow(!showChoices)} ref={ref}/>
            <div style={{
                position: "absolute",
                zIndex: 1,
            }}>
                {showChoices && <div style={{
                    backgroundColor: "white",
                    width: width,
                    borderRadius: "8px",
                    height: "20rem"
                }}>
                    {children}
                </div>
                }

            </div>
        </div>
    );
}

export default React.forwardRef(ServicePhone);
