import React from 'react';
import {css, StyleSheet} from 'aphrodite';
import Text from "../../../helpers/Text";
import "../../../css/layout.css"

function Category({pathToImage,text , colorImageBack}) {
    const style = StyleSheet.create({
        backgroundRounded: {
            backgroundColor:colorImageBack,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            borderRadius: "50%",
            height: "2.5rem",
            width: "2.5rem"
        },
        image: {
            width: "1.3rem"
        },
        borderCategory: {
            border: "1px solid #EAE9F2",
            borderRadius: '5px',
            alignItems:"center",
            padding: "0.5%",
            width: "20%",
            marginBottom:"1.2rem"
        },

    })
    return (
        <div className={css(style.borderCategory) + " row"} >
            <div className={css(style.backgroundRounded)}>
                <img className={css(style.image)} src={pathToImage} />
            </div>
            <div className="hor-20"></div>
            <div>
                <Text text={text} fontWeight={700} size={"1.1rem"}></Text>
            </div>
        </div>
    );
}

export default Category;
