import React, {useState} from 'react';
import Service from "./Service";
import Text from "../../../helpers/Text";
import {css, StyleSheet} from "aphrodite";
import check from "../../../../image/check.svg"

function ServiceText({id, title = "Service name", price = 10, handleChoices}) {
    const checkBox = {
        height: "1.2rem",
        width: "1.2rem",

        borderRadius: "50%",
        marginRight: "14px",
        border: "1px solid #979797",
    }
    const [isChosen, setChoice] = useState(false);

    // handleChoices.activationAndDeactivationOfCheck(isChosen);
    if (isChosen) {
        checkBox.backgroundColor = "#5454D4";
        checkBox.borderColor = "white";
    } else {
        checkBox.borderColor = "#979797";
    }

    const style = StyleSheet.create({
        checkBox: checkBox,
    })

    return (
        <>

        <Service
            firstComponent={
                <div className="alItCenter row">
                    <div className={css(style.checkBox) + " row alItCenter jccenter"}
                         onClick={() => {
                             setChoice(!isChosen);
                             // handleChoices.decideAddOrRemove(id, check);
                         }}
                    >
                        <img src={check}/>
                    </div>
                    <Text text={title} fontWeight={600} size={"1.05rem"}> </Text>
                </div>
            }
            secondComponent={
                <Text text={"$ " + price} color={"#979797"} size={"0.9rem"} fontStyle={"italic"} fontWeight={400}/>
            }/>
            <div style={{
                marginLeft:"0.8rem"
            }}></div>
        </>
    );
}

export default ServiceText;
