import React from 'react';
import {StyleSheet, css} from 'aphrodite';

export const Axis = {
    HORIZONTAL: "h",
    VERTICAL: "v"
}

function ScrollButton({selected = false, axis = Axis.VERTICAL}) {

    function selectAppropriateStyle(select, active, inactive) {
        return select ? active : inactive;
    }

    function selectAppropriateAxes(axis, list = [], horizontal, vertical) {
        const choosen = axis === Axis.HORIZONTAL ? horizontal : vertical;

        return list.concat(choosen);
    }

    const mainAxisActive = "3rem";
    const mainAxisInActive = "1.8em";
    const crossAxis = "0.35rem";

    const style = StyleSheet.create({
        common: {
            marginBottom: '0.5rem',
            borderRadius: "8px",
        },
        vertical: {
            width: crossAxis,
        },
        horizontal: {
            height: crossAxis,
        },
        active: {
            backgroundColor: "#5454D4"
        },
        activeHorizontal: {
            width: mainAxisActive
        },
        activeVertical: {
            height: mainAxisActive,
        },
        inactive: {
            backgroundColor: "#EAE9F2"
        },
        inactiveVertical: {
            height: mainAxisInActive,
        },
        inactiveHorizontal: {
            width: mainAxisInActive,
        },
        addHover: {
            ":hover": {}
        }
    });

    const common_list = [];
    common_list.push(selectAppropriateStyle(selected, style.active, style.inactive))
    common_list.push(style.common)

    const common_axes_list = selectAppropriateAxes(axis, common_list,
        [selectAppropriateStyle(selected, style.activeHorizontal, style.inactiveHorizontal), style.horizontal], // choose horizontal
        [selectAppropriateStyle(selected, style.activeVertical, style.inactiveVertical), style.vertical] // or vertical styling
    );

    const usedAxis = css(common_axes_list)
    return (
        <div className={usedAxis}></div>
    );
}

export default ScrollButton;
