import React from 'react';
import {css, StyleSheet} from 'aphrodite';
import {useMediaQuery} from "react-responsive";

const Text = ({
                  fontWeight = 600,
                  text,
                  color,
                  size,
                  center,
                  hover = {
                      cursor: "default"
                  }
              }) => {
    const isLaptopMax = useMediaQuery({
        query: "(max-device-width: 1024px) && (min-device-width: 897px)",
    });
    const isLaptopMin = useMediaQuery({
        query: "(max-device-width: 896px) && (min-device-width: 769px)",
    });
    const isTabletDevice = useMediaQuery({
        query: "(max-device-width: 768px)",
    });

    function convertToFontSize(size) {
        return size.toString() + "rem";
    }

    function applyFont(size, percent) {
        const float = parseFloat(size);
        const responsive = float - float / 100 * percent;
        return convertToFontSize(responsive);
    }

    function chooseFont(size) {
        if (isLaptopMax) {
            console.log("LAPTOP");
            return applyFont(size, 20)
        } else if (isLaptopMin) {
            console.log("LAPTOP MIN");
            return applyFont(size, 30)
        } else if (isTabletDevice) {
            console.log("TABLET");
            return applyFont(size, 40)
        }
        return size;
    }

    size = chooseFont(size);
    console.log(`true size : ${size}`);
    const style = StyleSheet.create({
            spanText: {
                fontFamily: "Gilroy",
                fontWeight: fontWeight,
                color: color,
                textAlign: center,
                fontSize: size,
            },
            addHover: {
                ":hover": hover
            }
        }
    );

    return (
        <span className={css(style.spanText, style.addHover)} onClick={() => console.log("Asdsad")}>
            {text}
        </span>
    );
};

export default Text;
