import React, {useState} from 'react';
import Images from "../Images";
import Paginate from "../../../helpers/scroll/Paginate";

function MobileImages(props) {
    const paginate = new Paginate(3, useState(0))

    function selectedImage(index) {
        return paginate.isSelected(index) ? "5rem" : "25rem";
    }

    const listImages = [
        props.portfolio.imageFirst,
        props.portfolio.imageSecond,
        props.portfolio.imageThird
    ]
    console.log(listImages);
    const listComponent = listImages
        .map((image, i) =>
            <div key={i} onClick={() => paginate.goPageDecide(i)}>
                <Images heightBox={selectedImage(i)} height={""} src={image}/>
            </div>);
    return (
        <>
            {listComponent}
            <div style={{
                margin: "20px"
            }}/>
        </>
    );
}

export default MobileImages;
