import React from 'react';
import Comment from "./Comment";
import {css, StyleSheet} from 'aphrodite';
import ScrollButton, {Axis} from "../../../helpers/scroll/ScrollButton";
import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../../responsive/Responsive";

function Comments(props) {
    const style = StyleSheet.create({
        edgesMargin: {
            marginRight:"10%",
            marginLeft :"10%",
            marginTop:"2.5%"
        }
    });
    const mobile = useMediaQuery(
        RESPONSIVE.MOBILE
    )
    const responsive = mobile ? "col" : "row";
    return (
        <>
        <div className={css(style.edgesMargin) + " row"}>
            <Comment/>
            <Comment/>
        </div>
            <div className="row" style={{
                justifyContent:"center"
            }}>
                <ScrollButton axis={Axis.HORIZONTAL}/>
                <ScrollButton axis={Axis.HORIZONTAL}/>
                <ScrollButton axis={Axis.HORIZONTAL} selected={true}/>
                <ScrollButton axis={Axis.HORIZONTAL}/>
            </div>
        </>
    );
}

export default Comments;
