import React from 'react';
import {StyleSheet, css} from 'aphrodite';
import Text from "../../helpers/Text";
import "../../css/layout.css"

function Category({pathToImage,text , colorImageBack}) {
    const style = StyleSheet.create({
        backgroundRounded: {
            backgroundColor:colorImageBack,
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
            borderRadius: "50%",
            height: "40px",
            width: "40px"
        },
        borderCategory: {
            border: "1px solid #EAE9F2",
            borderRadius: '5px',
            alignItems:"center",
            padding: "0.5%",
            width: "23.5%"
        },

    })
    return (
        <div className={css(style.borderCategory) + " row"} >
            <div className={css(style.backgroundRounded)}>
                <img src={pathToImage} />
            </div>
            <div className="hor-20"></div>
            <div>
                <Text text={text} fontWeight={700} size={"21px"}></Text>
            </div>
        </div>
    );
}

export default Category;
