import {StorePriceSite} from "../../store/StorePriceSite";
import {StorePriceSMM} from "../../store/StorePriceSMM";
import {StorePriceApp} from "../../store/StorePriceApp";
import {StorePriceDesign} from "../../store/StorePriceDesign";
import Paginate from "../../../../helpers/scroll/Paginate";

export class HandleChoices {
    constructor(state) {
        this.paginate = new Paginate(4, state);
        this.prices = [
            StorePriceSite,
            StorePriceSMM,
            StorePriceApp,
            StorePriceDesign
        ]
    }

    providePricesList() {
        return this.prices[this.paginate.currentPage];
    }

    activationAndDeactivationOfCheck(isChosen, checkBox) {
        console.log(isChosen);
        if (isChosen) {
            checkBox.backgroundColor = "#5454D4";
            checkBox.borderColor = "white";
        } else {
            checkBox.borderColor = "#979797";
        }
    }

    decideAddOrRemove(id, check) {
        console.log(`ID ${id} CHECK ${check}`)
    }
}
