import React, {useRef, useState} from 'react';
import Images from "../Images";
import Paginate from "../../../helpers/scroll/Paginate";
import DescriptionPortfolio from "../hover/DescriptionPortfolio";
import {useContainerDimensions} from "../../../helpers/hooks/useContainerDimensions";

function MobileImages(props) {
    const paginate = new Paginate(3, useState(0))

    function selectedImage(index) {
        return paginate.isSelected(index) ? "25rem" : "5rem";
    }

    const listImages = [
        props.portfolio.imageFirst,
        props.portfolio.imageSecond,
        props.portfolio.imageThird
    ]
    const myRefs = useRef(null);
    const {width, height} = useContainerDimensions(myRefs)

    const listComponent = listImages
        .map((image, i) =>
            <div key={i} onClick={() => paginate.goPageDecide(i)}>
                <Images ref={i === 2 ? myRefs : null} heightBox={selectedImage(i)} height={""}
                        borderRadiusBottomOff={i === 2} src={image}/>
            </div>);
    const style = {
        border: "1px solid #979797",

        borderTopRightRadius: "0px !important",
        borderTopLeftRadius: "0px !important",
        marginTop: "-2.5rem"
    }
    return (
        <>
            {listComponent}
            <DescriptionPortfolio width={width} body={props.portfolio.body} title={props.portfolio.title}
                                  additional_style={style}/>
            <div style={{
                margin: "20px"
            }}/>
        </>
    );
}

export default MobileImages;
