import React from 'react';
import {css, StyleSheet} from 'aphrodite';
import {useMediaQuery} from "react-responsive";

const Text = ({
                  fontWeight = 600,
                  text,
                  color,
                  size,
                  center,
                  hover = {
                      cursor: "default"
                  }
              }) => {
    const isLaptop = useMediaQuery({
        query: "(max-device-width: 1024px)",
    });
    function convertToFontSize(size) {
        return size.toString() + "rem";
    }

    function checkLaptop(size) {

        const float = parseFloat(size);
        const responsive = float - float / 100 * 20;
        console.log(`is Laptop : ${isLaptop}`);
        size = isLaptop ? responsive : float;
        return size
    }

    size = checkLaptop(size);
    console.log(`true size : ${size}`);
    const style = StyleSheet.create({
            spanText: {
                fontFamily: "Gilroy",
                fontWeight: fontWeight,
                color: color,
                textAlign: center,
                fontSize: convertToFontSize(size),
            },
            addHover: {
                ":hover": hover
            }
        }
    );

    return (
        <span className={css(style.spanText, style.addHover)} onClick={() => console.log("Asdsad")}>
            {text}
        </span>
    );
};

export default Text;
