import {StyleSheet, css} from 'aphrodite';
function ScrollButton({selected}) {

    const style = StyleSheet.create({
       active: {
           height:"40%",
           width:"7px",
           borderRadius:"8px",
           backgroundColor:"#5454D4"
       },
        inactive :{
            height:"35px",
            width:"7px",
            borderRadius:"8px",
            backgroundColor:"#5454D4"
        },
       addHover :  {
           ":hover":{

           }
       }
    });
    return (
        <div className={css(style.active)}></div>
    );
}

export default ScrollButton;
