import React from 'react';

import Images from "./Images";
import ScrollButton from "../../helpers/scroll/ScrollButton";
import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../responsive/Responsive";
import Scrolling from "../../helpers/scroll/Scrolling";
import HoverActions from "./hover/HoverActions";
import {useState} from "react";
import Paginate from "../../helpers/scroll/Paginate";


function RowImages({portfolio}) {
    const mobile = useMediaQuery(
        RESPONSIVE.MOBILE
    )
    const responsive = mobile ? "col alItCenter" : "row alItCenter";
    const componentList = [];

    for (let i = 0; i < portfolio.length; i++) {
        const desktopComponent = <>
            <Images flex="0.25"  src={portfolio[i].imageFirst}/>
            <HoverActions flex={"1"} child={<Images src={portfolio[i].imageSecond}/>}
                          title={portfolio[i].title} body={portfolio[i].body}
            />
            <Images flex="0.25" src={portfolio[i].imageThird}/>
            <div style={{
                margin: "20px"
            }}/>
        </>

        const mobileComponent = <>
            <Images heightBox={"5rem"} src={portfolio[i].imageFirst}/>
            <Images  src={portfolio[i].imageSecond}/>
            <Images src={portfolio[i].imageThird}/>
            <div style={{
                margin: "20px"
            }}/>
        </>
        mobile? componentList.push(mobileComponent) : componentList.push(desktopComponent);
    }
    const paginate = new Paginate(componentList.length, useState(0));
    return (
        <>
            <div className={responsive}>
                {componentList.filter((i, number) => number === paginate.currentPage)}
                <Scrolling length={componentList.length} paginate={paginate} onClick={() => {
                }}/>
            </div>
        </>
    );
}

export default RowImages;
