import React from 'react';
import Text from "../../../helpers/Text";
import TextHover from "../../../helpers/TextHover";

function HoverText({title , body}) {
    const style = {
        position:"absolute",
        bottom:-2,
        left:-2,
        width:"89%",
        padding:"1.5rem",
        backgroundColor:"white"
    }
    return (
        <div className="image-portfolio" style={style}>
            <Text  text={title} fontWeight={700}/>
            <br/>
            <Text text={body} />
        </div>
    );
}

export default HoverText;
