import React from 'react';
import {StyleSheet, css} from 'aphrodite';
import Text from "../../helpers/Text";
import ImagesAnimation from "./ImagesAnimation";
import Cartegories from "./Categories";
import Buttons from "../../helpers/Buttons";
const HelloAndImage = () => {
    const style = StyleSheet.create({
        containerRow: {
            flex:1,
            'display':"flex",
            flexDirection: "row",
            alignItems:"center",
        },
        firstColumn: {
            flex: 2,
            textAlign:"start"
        },
        secondColumn: {
            flex: 2,
        },
    })
    return (
        <div className={css(style.containerRow)}>
            <div className={css(style.firstColumn)}>
                <Text text="Улучшайте или создавайте ваш бизнес с IT B&SH" color={"#121212"} fontWeight={700} size={"3rem"} ></Text>
                <br/>
                <Text text="Мы современная IT компания которая поможет вам начать свой путь в техническом мире"  color={"#121212"} fontWeight={500} size={"1.3rem"}></Text>
                <br/><br/>
                <Buttons nameOfButton={"Подробнее"}></Buttons>
            </div>
            <div style={{
                flex:0.5
            }}></div>
            <div className={css(style.secondColumn)}>
                <ImagesAnimation></ImagesAnimation>
            </div>
        </div>
    );
};

export default HelloAndImage;
