import React from 'react';
import Text from "./Text";

function ListServices({
                    smm=false,
                    websites = false,
                    mobileApps = false,
                    design = false,
                      }) {

    function textEmphasize(choice, text){
        if(choice){
            return <Text text={text} color="#D24A6A" fontWeight={700} size="1.3rem"></Text>
        }
        return <Text text={text} color="#121212" fontWeight={400}></Text>
    }
    return (
        <div className="row"  style={{
            flex:0.45,
            alignItems:"start"
        }}>
            {textEmphasize(smm,"SMM")}
            <div style={{
                marginLeft:"3%"
            }}></div>
            {textEmphasize(websites,"Вэб-сайты")}
            <div style={{
                marginLeft:"3%"
            }}></div>
            {textEmphasize(mobileApps,"Приложения")}
            <div style={{
                marginLeft:"3%"
            }}></div>
            {textEmphasize(design,"Дизайн")}
        </div>
    );
}

export default ListServices;
