import React from 'react';
import Service from "./Service";
import drop from "../../../../image/drop_down.svg"

function ServiceButton({name = "Выберите сервис" , onClick}, ref) {
    return (
        <div onClick={onClick}>
            <Service
                ref={ref}
                containerWidth={"20rem"}
                firstComponent={name}
                secondComponent={
                    <img src={drop} alt=""/>
                }
                isButton={true}
            />
        </div>

    );
}

export default React.forwardRef(ServiceButton);
