import React from 'react';
import Header from "../../helpers/headers/Headers";
import Title from "../../helpers/Title";
import Comments from "./Comments";
import Buttons from "../../helpers/Buttons";
import {useMediaQuery} from "react-responsive";
import {RESPONSIVE} from "../../../responsive/Responsive";

function Reviews(props) {
    function margin(margin) {
        return <div style={{
            marginBottom: margin + "%"
        }}/>

    }
    const mobile = useMediaQuery(
        RESPONSIVE.MOBILE
    )
    const heightButton = mobile? "3rem":undefined;
    const widthButton = mobile?"16rem":"14rem";
    return (
        <>

            <div className="body global-margin">
                <div className="col alItCenter">
                    {margin("8")}
                    <Title title={"Отзывы клиентов"}/>
                    <Comments/>
                    {margin("4")}
                    <Buttons nameOfButton={"Оставить Коментарий"} height={heightButton} width={widthButton}/>
                </div>
            </div>
        </>

    );
}

export default Reviews;
