import React, {useRef, useState} from 'react';
import ServiceButton from "./service/ServiceButton";
import {useContainerDimensions} from "../../helpers/hooks/useContainerDimensions";

function DropDownService({handleChoices}) {
    const myRef = useRef();
    const width = useContainerDimensions(myRef).width;
    const [showChoices, setShow] = useState(false)
    return (
        <div>
            <ServiceButton onClick={()=>setShow(!showChoices)} ref={myRef}/>
            <div style={{
                position: "absolute",
                zIndex: 1,
            }}>
                {
                    showChoices && <div style={{
                        color: 'white',
                        backgroundColor: "gray",
                        width: width,
                        borderRadius: "8px",
                        height: "20rem"
                    }}>

                    </div>
                }

            </div>
        </div>
    );
}

export default DropDownService;
