import React from 'react';
import {StyleSheet, css} from 'aphrodite';

const Text = ({fontWeight = 600, text, color, size}) => {
    const style = StyleSheet.create({
            spanText: {
                fontFamily: "Gilroy"    ,
                fontWeight: fontWeight,
                color: color,
                fontSize: size,
            },
        }
    );
    const styles = StyleSheet.create({
        red: {
            backgroundColor: 'red'
        },

        blue: {
            backgroundColor: 'blue'
        },

        hover: {
            ':hover': {
                backgroundColor: 'red'
            }
        },

        small: {
            '@media (max-width: 600px)': {
                backgroundColor: 'red',
            }
        }
    });
    return (
        <span style={css(styles.red)} onClick={() => console.log("Asdsad")}>
            {text}
        </span>
    );
};

export default Text;
